import React from 'react' 
import  {Link} from 'react-router-dom'
import {connect} from 'react-redux'
import {startSetCategories,startRemoveCategory} from '../../actions/categoriesAction'
import Swal from 'sweetalert2'


function CategoriesList(props){
    if(props.categories.length == 0){
        props.dispatch(startSetCategories())
    }


    const handleRemove = (id) => {
        //const confirmRemove = window.confirm("Are you sure?")
        //if(confirmRemove){
           // props.dispatch(startRemoveCustomer(id))
        //}
        Swal.fire({
            title: 'Are you sure?',
            text: "You won't be able to revert this!",
            icon: 'warning',
            showCancelButton: true,
            confirmButtonColor: '#3085d6',
            cancelButtonColor: '#d33',
            confirmButtonText: 'Yes, delete it!'
          }).then((result) => {
            if (result.value) {
              Swal.fire(
                'Deleted!',
                'Your file has been deleted.',
                'success'
              )
              props.dispatch(startRemoveCategory(id))
            }

          })
    }

    return(
        <div>
            {
                props.categories ? (
                    <div>
                        <h2>Listing Categories-{props.categories.length}</h2>
            <table border="1">
                <thead>
                    <tr>
                        <th>Name</th>
                        <th>Remove</th>
                        <th>Actions</th>
                    </tr>
                </thead>
                <tbody>
                    {
                        props.categories.map(category => {
                            return <tr key={category._id}>
                                 <td>{category.name}</td>
                               <td>
                               <Link to={`/categories/edit/${category._id}`}>
                                    <button>EDIT</button></Link>
                                </td>
                                <td>
                                    <Link to="/categories">
                                    <button onClick={() => {
                                        handleRemove(category._id)
                                    }}>
                                        REMOVE
                                    </button>
                                </Link>
                                </td>
                            </tr>
                        })
                    }
                </tbody>
            </table>
           <h4><Link to="/categories/new"><button>ADD CATEGORIES</button></Link></h4>
           {/* <CategoryNew /> */}
            </div>

                ) :(
                    <div>
                        <p>loading....</p>
                    </div>
                )
            }
        </div>
    )
    }

 const mapStateToProps = (state) => {
     return {
         categories : state.categories
     }
 } 

export default connect(mapStateToProps)(CategoriesList)
