import React from 'react' 
import  {Link} from 'react-router-dom'
import {connect} from 'react-redux'
import {startSetNotes,startRemoveNote} from '../../actions/notesAction'
import Swal from 'sweetalert2'
import { startSetCategories } from '../../actions/categoriesAction';

//material-ui
import Card from '@material-ui/core/Card';
import Typography from '@material-ui/core/Typography';
import CardContent from '@material-ui/core/CardContent';
import { Container } from '@material-ui/core'
import Grid from '@material-ui/core/Grid'

import NoteAddIcon from '@material-ui/icons/NoteAdd';
import DeleteIcon from '@material-ui/icons/Delete';
import EditIcon from '@material-ui/icons/Edit';

function NotesList(props){
    //console.log("NOTES PROPS", props)

    if(props.notes.length== 0) {
        props.dispatch(startSetCategories())
        props.dispatch(startSetNotes())
    }

    const handleRemove = (id) => {
        Swal.fire({
            title: 'Are you sure?',
            text: "You won't be able to revert this!",
            icon: 'warning',
            showCancelButton: true,
            confirmButtonColor: '#3085d6',
            cancelButtonColor: '#d33',
            confirmButtonText: 'Yes, delete it!'
          })
          .then((result) => {
            if (result.value) {
              Swal.fire(
                'Deleted!',
                'Your file has been deleted.',
                'success'
              )
              props.dispatch(startRemoveNote(id))
            }

          })
    }

        return(
                <div>
                     <Container maxWidth="md" 
                                style={{ backgroundColor:"white",
                                         float : "left",
                                         margin:"3px"
                                        }}
                     >
                    {
                         props.notes? 
                        (
                            <Container>
                              <Grid container>
                                    <Grid item xs={6}>

                                        <h2 style={{textAlign:"center"}}>
                                        Listing Notes-{props.notes.length}
                                        </h2>

                                     </Grid>
                                     <Grid item xs={6}>
                                          <Link to="/notes/new">
                                             <NoteAddIcon titleAccess="Add Note" 
                                                          style={{marginTop : "23px"}}
                                        />
                                    </Link>
                                   </Grid>
                                </Grid>
                                <Grid item lg={12}>
                                {
                                  props.notes &&  props.notes.map(note => {
                                        return <Card key={note._id} 
                                                     style={{ backgroundColor:"pink",
                                                              float : "left",
                                                              margin:"3px"
                                                            }}
                                                >
                                                <CardContent >
                                                    <h2>{note.title}</h2>
                                                    <Typography>Description : {note.description}</Typography>

                                                    { props.categories.name !==  0 && 
                                                    (<Typography> Category :{note.category.name}</Typography>)}

                                                <Grid container style = {{marginTop : "20px"}}>

                                                    <Grid item xs={6}>
                                                        <Link to={`/notes/edit/${note._id}`}>
                                                            <EditIcon titleAccess="Edit Note" />
                                                        </Link>
                                                    </Grid>

                                                    <Grid item xs={6}>
                                                       <Link to="/notes">
                                                        <DeleteIcon titleAccess="Delete Note"
                                                                onClick={() =>{
                                                                    handleRemove(note._id)
                                                            }} 
                                                            style={{marginBottom:"20px"}}  
                                                        />
                                                    </Link>
                                               </Grid>
                                       </Grid>
                                        </CardContent>
                                    </Card> 
                                     })
                                }
                            </Grid>



                              {/* <table >
                                    <tbody>
                                        {
                                            props.notes.map(note => {
                                                // console.log("props map note",props.notes)
                                                return <tr key={note._id}>
                                                <td>{note.title}</td>
                                                <td>{note.description}</td>
                                                {/* {console.log("categories name",props.categories.name)} */}
                                           {/* { props.categories.name !==  0 && 
                                            (<td>{note.category.name}</td>)} */}
                                                {/* {console.log("p",note.category.name)} */}
                                                 {/* <td><Link to={`/notes/edit/${note._id}`}>
                                                        <button>EDIT</button>
                                                    </Link>
                                                </td> */}
                                                {/* <td>{note.photoPath}</td> */}
                                                {/* <td><Link to={`/notes/edit/${note._id}`}>
                                                        <button>EDIT</button>
                                                    </Link>
                                                </td>
                                                <td><Link to="/notes">
                                                    <button onClick={() =>{
                                                        handleRemove(note._id)
                                                    }}>REMOVE</button>
                                                    </Link>
                                                </td>
                                                </tr> 
                                             })
                                        }
                                    </tbody>
                                </table> */}
                    {/* <Link to="/notes/new"> <button>ADD NOTE</button></Link>  */}

                            </Container>
                        ) : (
                            <div>
                                <p>loading</p>
                            </div>
                        )
                    }
                    </Container>
                </div>
            )


    }
 const mapStateToProps = (state) => {
    return {
        categories:state.categories,//first must be categories n then notes
        notes: state.notes,


   }

}
export default connect(mapStateToProps)(NotesList)
