import React from 'react'
import ReactDOM from 'react-dom'
import App from './App'
import configureStore from './store/configureStore'
import {Provider} from 'react-redux'
import { startSetUser } from './actions/userAction';
import {startSetCategories} from './actions/categoriesAction'
import {startSetNotes} from './actions/notesAction'

//calling or invoking configureStore() function
const store=configureStore()

//to see the initial value of our store we do this
console.log(store.getState())

//subscribe method
store.subscribe(()=>{
    console.log(store.getState())//now we have the paricular state value
})

//when we refresh the page we will loose the user object stored in our redux store so we need to handle page reloads
//handle page reloads
if(localStorage.getItem('authToken')){
    //if the token is there then the user has logged in
   store.dispatch(startSetUser())

   store.dispatch(startSetCategories())//categories must be first
   store.dispatch(startSetNotes())//notes has categories so we need to have store.dispatch(startSetCategories()) first

}
const jsx=(
    <Provider store={store}>
    {/* store is a property */}
       <App/>
    </Provider>
)

ReactDOM.render(jsx,document.getElementById('root'))

//we need to coonect our components to get access to the dispatch() method n we need to pass the <App/> inside our provider
