import React from 'react'
import {Link,BrowserRouter,Route,Switch} from 'react-router-dom'
import {connect} from 'react-redux'
import {startLogout} from './actions/userAction'

//users imports
import Home from './components/Users/Home'
import Register from './components/Users/Register'
import Login from './components/Users/Login'
import Account from './components/Users/Account'

//categories import
import CategoriesList from './components/Categories/CategoriesList'//1)list
import CategoriesShow from './components/Categories/CategoriesShow'//2)show
import CategoriesNew from './components/Categories/CategoriesNew' //3)New
import CategoriesEdit from './components/Categories/CategoriesEdit'//4)Edit

import NotesList from './components/Notes/NotesList'
import NotesShow from './components/Notes/NoteShow'
import NotesNew from './components/Notes/NoteNew'
import NotesEdit from './components/Notes/NotesEdit'

function App(props){
    console.log('App Component Props',props)

    const handleLogout=()=>{
        const status = window.confirm('are you sure to logout')
        if(status){
        props.dispatch(startLogout())
        }
    }

    return(
        <BrowserRouter>
        <div>
            <h2>Welcome to App Component</h2>
            <Link to="/">Home</Link>

       {
        //conditinal rendering to chk whether user has logged in  or not
        //if user obj is empty means user has not logged in so we need to show only register n login(user object is 0 for the very first time)
        Object.keys(props.user).length==0 ? (
            <div>
               <Link to="/users/register">Register</Link>
               <Link to="/users/login">Login</Link>
            </div>

        ):(//if the user obj is not empty ie we get some value(user obj value) then user has logged in to the application n the length is 3 username ,email n password
            <div>
                <Link to="/users/account">Account</Link>
                <Link to="/categories">Categories</Link>
                <Link to='/notes'>Notes</Link>

                <Link to="#" onClick={()=>{handleLogout()//inline function
                 }}>Logout</Link>
            </div>

        )
     }
</div>

<Switch>
<Route path="/" component={Home} exact={true} />
<Route path="/users/register" component={Register} exact={true}/>
<Route path="/users/login" component={Login}/>

<Route path="/users/account" component={Account}/>

<Route path="/categories" component={CategoriesList} exact={true} />
<Route path="/categories/new" component={CategoriesNew}/>
<Route path="/categories/edit/:id" component={CategoriesEdit} />
<Route path="/categories/:id" component={CategoriesShow} />

<Route path='/notes' component={NotesList} exact={true}/>
<Route path="/notes/new" component={NotesNew}/>
<Route path="/notes/edit/:id" component={NotesEdit} />
<Route path="/notes/:id" component={NotesShow} />

</Switch>         
</BrowserRouter> 
)

}

//we need to read the data (user obj ) from the redux store
const mapStateToProps=(state)=>{//now we have access to the state value
    return {
         user : state.user
    }

}

export default connect(mapStateToProps)(App)

//<Switch> is like a switch case whenever a certain condition is met it will execute the code n break it
