import React from 'react'
import {connect} from 'react-redux'
import { startRegister } from '../../actions/userAction';

class Register extends React.Component{
    constructor(){
        super()
        this.state={
            username:'',
            email:'',
            password:''

        }

    }
    //methods
    handleChange=(e)=>{
        this.setState({
            //setState becoz we are doing redux n not react
            [e.target.name]:e.target.value
        })
    }

    handleSubmit=(e)=>{
        e.preventDefault();
        const formData={
            username:this.state.username,
            email:this.state.email,
            password:this.state.password
        }
        console.log("User Registration Form data",formData)
        //since we have used connect we get access to our dispatch method via props

        //we need to programatically redirect to our login page
        const redirect=()=>{
          return this.props.history.push('/users/login')
        }

        this.props.dispatch(startRegister(formData,redirect))//passing functions as arguements to another functiins
        //startRegister is an action--this action we need to create in our actions folder-->userActions.js
    }



    render(){
        return(
            <div>
                <h2>Register with us</h2>
                <form onSubmit={this.handleSubmit}>
                    <label htmlFor="username">UserName:</label>
                    <input type="text" id="username" name="username" 
                    placeholder="enter username" value={this.state.username} 
                    onChange={this.handleChange}/><br/>

                    <label htmlFor="email">Email:</label>
                    <input type="text" id="email" name="email" 
                    placeholder="enter email" value={this.state.email} 
                    onChange={this.handleChange}/><br/>

                    <label htmlFor="password">Password:</label>
                    <input type="text" id="password" name="password" 
                    placeholder="enter password" value={this.state.password} 
                    onChange={this.handleChange}/><br/> 

                    <input type="submit" value="submit" />

                </form>
            </div>
        )
    }
}

//make it as  a wrapped component
export default connect()(Register)
