import React from 'react'
import {withRouter} from 'react-router-dom'
import {connect} from 'react-redux'
import {findCategory} from '../../selectors/categoriesSelector'

class CategoryForm extends React.Component{

    constructor(props){
        console.log('Categories form constructor',props)
        super(props)
            this.state={
                name:props.category ? props.category.name:'',

            }
        }

        handleChange=(e)=>{
            this.setState({
                [e.target.name]:e.target.value
            })
        }

        handleSubmit=(e)=>{//we have access to the event object
            e.preventDefault()
            const formData={
                name:this.state.name,
            }
            // from the form we are invoking a method defined in the parent comonent ie CategoriesNew
            this.props.handleSubmit(formData)//fun invocation
        }

render(){
    return(
        <div>
            <form onSubmit={this.handleSubmit}>

                <label htmlFor="name">name</label>
                <input type='text' value={this.state.name} 
                name="name" id="name" onChange={this.handleChange}/><br/>

                <input type="submit"/>
            </form>
        </div>
    )
}
}
const mapStateToProps=(state,props)=>{
     console.log('form',props)//we need to use withRouter()
    const id=props.match.params.id
    return {
        category:findCategory(state.categories,id)
}
}
export default withRouter(connect(mapStateToProps)(CategoryForm))

//when we use withRouter() our form component will have access to history,location n match
//here we are embedding our form in add n edit parent components n history ,location n match will be available if it is loaded from a route,but in this case our form is embedded inside in our forms so we dont have access to history ,location n props so we need to use withRouter()
//withRouter() is a HOC
